import { createReducer, on, createFeatureSelector, createSelector } from "@ngrx/store";
import { loginFailure, loginRequest, loginSuccess } from "./auth.actions";
export const initialState = {
  token: null,
  userName: null
};

const _authReducer = createReducer(initialState, on(loginRequest, (state, {
  userName,
  password
}) => {
  return Object.assign(Object.assign({}, state), {
    loginError: null
  });
}), on(loginSuccess, (state, {
  userName,
  token
}) => {
  return Object.assign(Object.assign({}, state), {
    token: token,
    userName: userName
  });
}), on(loginFailure, (state, {
  error
}) => {
  return Object.assign(Object.assign({}, state), {
    loginError: error,
    token: null,
    user: null
  });
}));

export function authReducer(state, action) {
  return _authReducer(state, action);
}
export const selectAuthState = createFeatureSelector('auth');
export const selectToken = createSelector(selectAuthState, state => state.token);
export const selectUser = createSelector(selectAuthState, state => state.userName);
export const selectError = createSelector(selectAuthState, state => state.loginError);
