import { createReducer, on, createFeatureSelector, createSelector } from "@ngrx/store";
import { getMessageRequest, getMessageFailure, getMessageSuccess } from "./getMessage.actions";
export const initialState = {
  messages: null
};

const _getMessageReducer = createReducer(initialState, on(getMessageRequest, (state, {}) => {
  return Object.assign(Object.assign({}, state), {
    messageError: null
  });
}), on(getMessageSuccess, (state, {
  messages
}) => {
  return Object.assign(Object.assign({}, state), {
    messages: messages
  });
}), on(getMessageFailure, (state, {
  error
}) => {
  return Object.assign(Object.assign({}, state), {
    messageError: error,
    message: null,
    user: null
  });
}));

export function getMessageReducer(state, action) {
  return _getMessageReducer(state, action);
}
export const selectMessageState = createFeatureSelector('getMessage');
export const selectMessages = createSelector(selectMessageState, state => state.messages);
