import { createEntityAdapter } from '@ngrx/entity';
import { createReducer, on, createFeatureSelector, createSelector } from "@ngrx/store";
import { getMessageFailure, getMessageSuccess } from "./getMessage.actions";
export const messageAdapter = createEntityAdapter({});
export const initialState = messageAdapter.getInitialState({});

const _getMessageReducer = createReducer(initialState, on(getMessageSuccess, (state, action) => {
  return messageAdapter.setAll(action.messages, Object.assign({}, state));
}), on(getMessageFailure, (state, {
  error
}) => {
  return Object.assign(Object.assign({}, state), {
    messageError: error,
    message: null,
    user: null
  });
}));

export function getMessageReducer(state, action) {
  return _getMessageReducer(state, action);
}
export const selectMessageState = createFeatureSelector('getMessage');
export const messageSelectors = messageAdapter.getSelectors();
export const getMessages = createSelector(selectMessageState, messageSelectors.selectAll);
